function getUrlParam(a){let reg=new RegExp("(^|&)"+a+"=([^&]*)(&|$)");let r=window.location.search.substr(1).match(reg);if(r!=null){return decodeURIComponent(r[2])}return null}function timestampToTime(a){let date=new Date(parseInt(a));let Y=date.getFullYear()+"-";let M=(date.getMonth()+1<10?"0"+(date.getMonth()+1):date.getMonth()+1)+"-";let D=date.getDate()+" ";let h=date.getHours()+":";let m=(date.getMinutes()<10?"0"+(date.getMinutes()):date.getMinutes())+":";let s=(date.getSeconds()<10?"0"+(date.getSeconds()):date.getSeconds());return Y+M+D+h+m+s}function toRelativeDate(a){let today0=new Date(new Date().toLocaleDateString()).getTime();let result=(a-today0)/1000/24/60/60;if(result>=0&&result<1){return 0}else{if(result>=1&&result<2){return 1}else{if(result>=2&&result<3){return 2}else{return -1}}}}function toRelativeDateString(a){let str=toRelativeDate(a);if(str===0){return"今天"}else{if(str===1){return"明天"}else{if(str===2){return"后天"}else{return"其他"}}}}function timestampToTimeOfTheDay(a){let date=new Date(a);return date.getHours()+":"+date.getMinutes()}function getDateWeek(a){let day=new Date(a);let today=["星期日","星期一","星期二","星期三","星期四","星期五","星期六"];return today[day.getDay()]}function saveData2Ses(a){$.each(a,function(b,c){if(c instanceof Object){window.sessionStorage.setItem(b,JSON.stringify(c))}else{window.sessionStorage.setItem(b,c)}})}function setCookie(a,c,b){let cookieStr=a+"="+c;if(b===0){cookieStr+="; expires=0"}else{if(b<0){cookieStr+="; expires=-1"}else{let expires=new Date();expires.setTime(expires.getTime()+b*60*60*1000);cookieStr+="; expires="+expires.toUTCString()}}document.cookie=cookieStr}function getCookie(a){let reg=new RegExp("(^| )"+a+"=([^;]*)(;|$)");let cookieStr=document.cookie;if(cookieStr!==""){let arr=cookieStr.match(reg);if(arr[2]!==""){return arr[2]}}return null}function myAlert(a){if($("#alert").length===0){$.ajax({url:"alert",type:"get",async:true,dataType:"html",success:function(b){$("body").append(b);$("#alert").modal();$("#alert-message").text(a)},error:function(b){alert("----ajax请求加载自定义提示框出错！----\n\n"+b.responseText)}})}};