function myAlert(a){let alert=$("#alert");if($(alert).length===0){$.get(static_components.component_alert.curl,function(b){$("body").append(b);$("#alert").modal();$("#alert-message").text(a)})}$(alert).modal();$("#alert-message").text(a)}function ajaxFailed(a){$.get(static_components.component_load_error.curl,function(b){$(a).html(b)})}function ajaxNoContent(a){$.get(static_components.component_none_result.curl,function(b){$(a).html(b)})}function loadingAnimation(a){return new Promise(function(b){$.get(static_components.component_loading_animation.curl,function(c){$(a).html(c);b()})})}function setCookie(a,c,b){let cookieStr=a+"="+c;if(b===0){cookieStr+="; expires=0"}else{if(b<0){cookieStr+="; expires=-1"}else{let expires=new Date();expires.setTime(expires.getTime()+b*60*60*1000);cookieStr+="; expires="+expires.toUTCString()}}document.cookie=cookieStr}function getCookie(a){let reg=new RegExp("(^| )"+a+"=([^;]*)(;|$)");let cookieStr=document.cookie;if(cookieStr!==""){let arr=cookieStr.match(reg);if(arr[2]!==""){return arr[2]}}return null};