function DateToTime(a){let Y=a.getFullYear()+"/";let M=(a.getMonth()+1<10?"0"+(a.getMonth()+1):a.getMonth()+1)+"/";let D=(a.getDate()+1<10?"0"+(a.getDate()):a.getDate());return Y+M+D}function anchorPointJump(b,a){$(window).on("scroll",function(){var c=$(window).scrollTop();if(c>0){document.getElementById(b).removeAttribute("hidden")}else{document.getElementById(b).setAttribute("hidden",true)}});$("#"+a).click(function(){$("html,body").animate({scrollTop:($($(this).attr("href")).offset().top-50)},500)})};